import toThousands from "thousands";
import Big from "big.js";
import {
  disableBodyScroll,
  enableBodyScroll
  // clearAllBodyScrollLocks
} from "body-scroll-lock";

function copyFunction(copyId, copyText) {
  // copy first then select for common use
  let Url2;
  if (copyText) {
    Url2 = copyText;
  } else {
    Url2 = document.getElementById(copyId).innerText;
  }
  let oInput = document.createElement("input");
  oInput.value = Url2;
  document.body.appendChild(oInput);
  oInput.select();
  document.execCommand("Copy");
  oInput.remove();
  // Browser compatibility
  // if (document.body.createTextRange) {
  //   let range = document.body.createTextRange();
  //   range.moveToElementText(document.getElementById(copyId));
  //   range.select();
  // } else if (window.getSelection) {
  //   let selection = window.getSelection();
  //   let range = document.createRange();
  //   range.selectNode(document.getElementById(copyId));
  //   selection.removeAllRanges();
  //   selection.addRange(range);
  // } else {
  //   // alert("none");
  // }
}

function getType(obj) {
  if (obj instanceof Element) {
    return "element";
  }
  const type = Object.prototype.toString.call(obj);
  return {
    "[object Boolean]": "boolean",
    "[object Number]": "number",
    "[object String]": "string",
    "[object Function]": "function",
    "[object Array]": "array",
    "[object Date]": "date",
    "[object RegExp]": "regExp",
    "[object Undefined]": "undefined",
    "[object Null]": "null",
    "[object Object]": "object"
  }[type];
}

function deepClone(obj) {
  const type = getType(obj);
  let clone;
  if (type === "array") {
    clone = [];
    for (let i = 0; i < obj.length; i++) {
      clone.push(deepClone(obj[i]));
    }
  } else if (type === "object") {
    clone = {};
    for (let key in obj) {
      clone[key] = deepClone(obj[key]);
    }
  } else {
    return obj;
  }
  return clone;
}

function getBalance(num1, num2) {
  if (!num2) return "";
  if (num1 !== 0 && !num1) return;
  return Big(num1)
    .div(num2)
    .toFixed();
}
function lockScroll(islock) {
  if (islock) {
    disableBodyScroll();
  } else {
    enableBodyScroll();
  }
}

export default {
  lockScroll,
  disableBodyScroll,
  enableBodyScroll,
  Big,
  getBalance,
  toThousands,
  deepClone,
  getType,
  copyFunction
};
