import QrcodeDecoder from "qrcode-decoder";
import crypto from "@/libs1/crypto";
export default {
  data: function data() {
    return {
      winWidth: window.innerWidth,
      winHeight: window.innerHeight,
      // orientation: window.orientation || window.screen.orientation.angle || 0
      orientation: "orientation" in window ? window.orientation : "orientation" in window.screen ? window.screen.orientation.angle : 0
    };
  },
  mounted: function mounted() {
    var _this = this;

    window.addEventListener("resize", function () {
