    }
  },
  methods: {
    readQRimg: function readQRimg() {
      this.$parent.exitCamera();
      document.getElementById("file").click();
    },
    getUrl: function getUrl(type) {
      var _this2 = this;

      var elem = document.getElementById("file");
      console.log(elem);
      var qr = new QrcodeDecoder();
      var url = null;

      if (type === "img-url") {
        url = elem.src;
      } else if (type === "file-url" && elem.files.length > 0) {
        url = this.getObjectURL(elem.files[0]);
      }

      qr.decodeFromImage(url).then(function (res) {
        console.log(res.data);
        var receiveAddress = JSON.parse(res.data);

        if (crypto.isValidAddress(receiveAddress.address, _this2.$config.NET_BYTE)) {
          _this2.$store.commit("update_sendInfo", {
            recipient: receiveAddress.address,
            amount: receiveAddress.amount,
            description: receiveAddress.description
          });

          _this2.$router.replace({
            path: "/home/send"
          });
        }
      }).catch(function (err) {
        console.error(err);
        alert("The qr code you selected is illegal in the site");
      });
    },
    // Get the preview image path
    getObjectURL: function getObjectURL(file) {
      var url = null;

      if (window.createObjectURL !== undefined) {
        // basic
        url = window.createObjectURL(file);
      } else if (window.URL !== undefined) {
        // mozilla(firefox)
        url = window.URL.createObjectURL(file);
      } else if (window.webkitURL !== undefined) {
        // webkit or chrome
        url = window.webkitURL.createObjectURL(file);
      }

      return url;
    }
  }
};
