import seedLib from "@/libs1/seed";
export default {
  name: "PwdText",
  props: {
    type: String,
    promp: {
      type: String,
      default: "Please input your password."
    },
    height: {
      type: String,
      default: "40px"
    },
    id: String
  },
  data: function data() {
    return {
      keyPromp: "",
      isShow: false,
      key1: "",
      key2: "",
      keyArr: [],
      flag: true,
      textStyle: {
        height: this.height,
        width: "100%"
      }
    };
  },
  methods: {
    keyDown: function keyDown(event) {
      // If Press Delete key
      if (event.keyCode == 8) {
        //if Press Delete & cmd Key
        if (event.metaKey) {
          this.key1 = "";
          this.key2 = "";
          this.keyArr = [];
        } else {
          this.flag = false;
          this.keyArr = this.keyArr.join().replace(/,/g, "").split("");
          var num = event.target.selectionEnd - event.target.selectionStart;

          if (num == 1 || num == 0) {
            this.keyArr.splice(event.target.selectionStart - 1, 1);
          } else {
            this.keyArr.splice(event.target.selectionStart, num);
          }

          this.key2 = this.keyArr.join().replace(/,/g, "");
        }
      } else {
        this.flag = true;
      }
    },
    input: function input() {
      if (this.flag && this.key1.replace(/\*/g, "") != "") {
        this.passwordText();
      }
    },
    passwordText: function passwordText() {
      this.keyArr.push(this.key1.replace(/\*/g, ""));
      this.key2 = this.keyArr.join().replace(/,/g, "");
      this.key1 = this.key1.replace(/./g, "*");
    },
    isCorrect: function isCorrect(password) {
      var userInfo = window.localStorage["encryptSeed"];

      try {
        var seedWords = seedLib.decryptSeedPhrase(JSON.parse(seedLib.decryptSeedPhrase(userInfo, password)).encrSeed, password).split(",");

        if (this.type === "priKey") {
          this.key1 = this.$store.state.wallets[this.$store.state.currentIndex].keyPair.privateKey;
        }

        if (this.type === "seed") {
          this.key1 = seedWords;
        }

        this.keyPromp = "";
        this.key2 = "";
        this.keyArr = [];
        return true;
      } catch (err) {
        this.keyPromp = "Your password is wrong."; // alert("Your password is wrong");
      }
    },
    clickShow: function clickShow(password) {
      if (!this.isShow) {
        if (this.isCorrect(password)) {
          this.isShow = true;
        }
      } else {
        this.key1 = "";
        this.isShow = false;
      }
    }
  }
};
