import Vue from "vue";
import "./plugins/axios";
import App from "./App.vue";
import router from "./router";
import store from "./store";
import axios from "axios";
import vuetify from "./plugins/vuetify"; //import compoment for wallet

import * as constants from "@/constants";
import "./static/css/reset.css";
import LoginHeader from "./components/LoginHeader";
import MidTextarea from "./components/MidTextarea";
import MainHeader from "./components/MainHeader";
import CopyButton from "@/components/CopyButton.vue";
import utils from "@/utils/common.js";
Vue.component("LoginHeader", LoginHeader);
Vue.component("MidTextarea", MidTextarea);
Vue.component("MainHeader", MainHeader);
Vue.component("CopyButton", CopyButton);
Vue.config.productionTip = false;
window.constants = constants;
Vue.prototype.$utils = utils;

(async () => {
  Vue.config.productionTip = false; //inject config into Vue

  let res = await axios.get("/config.json");
  let config = res.data;
  config.apiEndPoint = config.net && config.net === "testnet" ? config.apiTestnetEndPoint : config.apiMainnetEndPoint;
  axios.defaults.baseURL = config.apiEndPoint;
  config.NET_BYTE = {
    testnet: constants.TESTNET_BYTE,
    Mainnet: constants.MAINNET_BYTE
  }[config.net];
  config.vsysEndPoint = {
    testnet: config.vsysTestnetEndPoint,
    Mainnet: config.vsysMainnetEndPoint
  }[config.net];
  Vue.prototype.$config = config; // Modify vuetify theme

  const themeColor = {
    red: "#FF546A",
    yellow: "#FFA100",
    purple: "#8241F9",
    blue: "#068EF1",
    cyan: "#00B8AF"
  }[config.theme];
  vuetify.framework.theme.themes.light.primary = themeColor;
  const themeObj = {
    "--urlHome": "img_homecard",
    "--urlInfo": "img_infocard",
    "--urlShift": "img_walletselectedcard",
    "--urlControl": "ic_amount_control_activited"
  };

  for (let key in themeObj) {
    document.body.style.setProperty(key, "url(" + require(`@/assets/${config.theme}/${themeObj[key]}.svg`) + ")");
  }

  document.body.style.setProperty("--theme", themeColor);
  new Vue({
    router,
    store,
    vuetify,
    render: h => h(App)
  }).$mount("#app");
})();
