import seedLib, { Seed } from "@/libs1/seed";
export default {
  data: function data() {
    return {
      password: "",
      isPwdError: false,
      errorMsg: ""
    };
  },
  methods: {
    Login: function Login() {
      var cacheInfo = window.localStorage["encryptSeed"];
      var userInfo = cacheInfo;

      try {
        var seedWords = seedLib.decryptSeedPhrase(JSON.parse(seedLib.decryptSeedPhrase(userInfo, this.password)).encrSeed, this.password);
        this.seed = new Seed(seedWords, 0, this.$config.NET_BYTE);
        var walletCount = Number(window.localStorage["walletCount"]);
        this.$store.commit("ADD_INFO", {
          password: this.password,
          seed: seedWords,
          walletCount: walletCount
        });

        for (var i = 0; i < walletCount; i++) {
          var seed = new Seed(seedWords, i, this.$config.NET_BYTE);
          this.$store.commit("add_wallets", {
            index: i,
            data: {
              address: seed.address,
              keyPair: seed.keyPair,
              alias: JSON.parse(window.localStorage["alias"])[i],
              balance: "",
              vsysBalance: 0,
              transactionCount: "",
              updateTime: ""
            }
          });
        }

        this.errorMsg = "";
        this.isPwdError = false;
      } catch (err) {
        this.isPwdError = true;
        this.errorMsg = "Password error, you can restore your account by your seed.";
      }

      if (!this.isPwdError) {
        if (this.$store.state.loginType === "send") {
          this.$router.replace({
            path: "/home/shift",
            query: this.$route.query
          });
        } else {
          this.$router.replace({
            path: "/home"
          });
        }
      }
    }
  }
};
