import seedLib, { Seed } from "@/libs1/seed";
import { mapState } from "vuex";
export default {
  name: "shift",
  data() {
    return {
      seedWords: "",
      addressOrSeeds: true
    };
  },
  created() {
    // init seedWords
    let userInfo = window.localStorage["encryptSeed"];
    try {
      this.seedWords = seedLib.decryptSeedPhrase(
        JSON.parse(seedLib.decryptSeedPhrase(userInfo, this.password)).encrSeed,
        this.password
      );
    } catch (err) {
      console.log(err);
    }
    // update wallets
    this.$store.dispatch("update_token", this.$config.tokenId);
    for (let i in this.wallets) {
      this.$store.dispatch("update_balance", {
        index: i,
        tokenId: this.$config.tokenId
      });
    }
  },
  computed: {
    ...mapState(["wallets", "currentIndex", "unity", "password"])
  },
  methods: {
    addAddress() {
      if (Object.keys(this.wallets).length > 9) return;
      // alert(`Sorry! You can't create more than 10 wallets`);
      let index = Object.keys(this.wallets).length;
      let seed = new Seed(this.seedWords, index, this.$config.NET_BYTE);
      this.addWalletInfo(seed);
      // requeset new wallet transaction data
      this.$store.dispatch("update_balance", {
        index: index,
        tokenId: this.$config.tokenId
      });
    },

    addWalletInfo(seed) {
      //Add Store Info
      this.$store.commit("add_wallets", {
        index: window.localStorage["walletCount"],
        data: {
          address: seed.address,
          keyPair: seed.keyPair,
          alias: "",
          balance: 0,
          vsysBalance: 0,
          transactionCount: 0,
          updateTime: new Date().getTime()
        }
      });
      this.$store.commit("ADD_INFO", {
        walletCount: Number(window.localStorage["walletCount"])
      });
      //Add LocalStorage Info
      let aliasObj = JSON.parse(window.localStorage["alias"]);
      aliasObj[window.localStorage["walletCount"]] = "";
      window.localStorage["alias"] = JSON.stringify(aliasObj);
      window.localStorage["walletCount"]++;
    },

    selectAddress(key) {
      // Storage vuex
      this.$store.commit("ADD_INFO", {
        currentIndex: Number(key)
      });
      if (this.$store.state.loginType === "send") {
        this.$router.replace({ path: "/home/send", query: this.$route.query });
      } else {
        this.$router.replace({ path: "/home" });
      }
    },

    A_S(as) {
      if (this.addressOrSeeds == as) this.addressOrSeeds = !this.addressOrSeeds;
    }
  }
};
