export default {
  data() {
    return {
      langs: [
        {
          value: "English"
        },
        {
          value: "繁体中文"
        },
        {
          value: "한국어"
        }
      ],
      times: [5, 10, 15],
      //default lang English
      selectedLang: "English",
      selectedTime: Number(window.localStorage["sessionTimeout"]),
      isDisabled: false,
      dialog: false,
      dialog1: false,
      dialog2: false
    };
  },
  methods: {
    resetWallet() {
      this.$store.dispatch("reset_state").then(() => {
        window.localStorage.clear();
        this.$router.replace({ path: "/create" });
      });
    },
    langDisabled(lang) {
      return lang === this.langs[1].value || lang === this.langs[2].value;
    },
    selectLang(lang) {
      if (this.langDisabled(lang)) return;
      this.selectedLang = lang;
      this.dialog1 = false;
    },
    selectTime(time) {
      this.selectedTime = time;
      window.localStorage["sessionTimeout"] = time;
      this.dialog2 = false;
    }
  }
};
