.bal-inner {
  width: 30vw;
}
.bal-outer {
  vertical-align: middle;
  display: inline-block;
  width: 40vw;
  overflow-x: scroll;
  overflow-y: hidden;
  scrollbar-width: none;
}
.bal-outer::-webkit-scrollbar {
  display: none;
}
.v-dialog__content {
  display: flex;
  align-items: flex-end;
}
.v-dialog__content >>> .v-dialog--active {
  border-radius: 0;
  margin: 0;
  /* max-height: 100%; */
}
.send-item {
  padding: 14px 0 14px 0;
  border-bottom: 1px solid #f5f5f7;
  line-height: 16px;
}
div p {
  margin-bottom: 12px;
}
.send-title {
  font-size: 15px;
  color: #444444;
}
.v-text-field >>> .v-label {
  font-family: "SFProText-Regular", "SFProText", "SF Pro Text Regular";
  font-weight: 400;
  font-size: 15px;
  color: #9e9e9e;
  max-width: 100%;
}
.v-textarea >>> textarea {
  height: 40px;
  margin: 16px 0 16px 0 !important;
  font-size: 15px;
  padding: 0 0 0 0 !important;
  scrollbar-width: none;
}
.v-textarea >>> textarea::-webkit-scrollbar {
  display: none;
}
