export default {
  props: {
    source: String,
    title: String
  },
  data: () => ({
    drawer: false,
    drawerHeight: window.innerHeight
  }),
  created() {
    this.menuLists = this.menuListsComputed();
  },
  mounted() {
    window.addEventListener("resize", () => {
      this.drawerHeight = window.innerHeight;
    });
  },
  watch: {
    drawer(nv) {
      //update drawer state in store
      this.$store.commit("update_header", nv);
    }
  },
  methods: {
    menuListsComputed() {
      let configTheme = this.$config.theme;
      return [
        {
          value: "Wallet Home",
          path: "/home/home",
          name: "/home",
          // use require() or put imgs into './public'
          // img_1: 'img/cyan/ic_assets_activited.svg',
          // img_2: 'img/ic_assets_normal.svg'
          img_1: require(`@/assets/${configTheme}/ic_assets_activited.svg`),
          img_2: require("@/assets/ic_assets_normal.svg"),
          img_3: require("@/assets/ic_assets_grey.svg")
        },
        {
          value: "My Information",
          path: "/home/info",
          img_1: require(`@/assets/${configTheme}/ic_info_activited.svg`),
          img_2: require("@/assets/ic_info_normal.svg"),
          img_3: require("@/assets/ic_info_grey.svg")
        },
        {
          value: "Switch Account",
          path: "/home/shift",
          img_1: require(`@/assets/${configTheme}/ic_wallet_activited.svg`),
          img_2: require("@/assets/ic_wallet_normal.svg"),
          img_3: require("@/assets/ic_wallet_grey.svg")
        },
        {
          value: "Show VSYS",
          path: "",
          img_1: require("@/assets/ic_vsys_normal.svg"),
          img_2: require("@/assets/ic_vsys_normal.svg"),
          img_3: require("@/assets/ic_vsys_grey.svg")
        },
        {
          value: "Setting",
          path: "/home/setting",
          img_1: require(`@/assets/${configTheme}/ic_setting_activited.svg`),
          img_2: require("@/assets/ic_setting_normal.svg"),
          img_3: require("@/assets/ic_setting_grey.svg")
        },
        {
          value: "Logout",
          path: "/",
          img_1: "",
          img_2: require("@/assets/ic_logout_normal.svg"),
          img_3: require("@/assets/ic_logout_normal.svg")
        }
      ];
    },
    routerPage(page, mobile) {
      if (!page) return;
      if (this.$route.path == page && mobile) {
        this.drawer = !this.drawer;
      } else if (this.$route.path == page && !mobile) {
        return;
      } else {
        this.$router.replace({ path: page });
      }
    }
  }
};
