.popContainer {
  z-index: 1000;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  display: flex;
  flex-direction: column;
}
.flex-grow-1 {
  flex-grow: 1;
}
.add-radius {
  border: 1px solid #00b8af;
}
.black-bg {
  background: rgba(0, 0, 0, 0.8);
}
.middle-style {
  width: 62vw;
  height: 62vw;
}
.middle-style-big {
  width: 470px;
  height: 470px;
}
.middle-style-landscape {
  width: 50vmin;
  height: 50vmin;
}
.scan-font {
  color: #9e9e9e;
  font-size: 5vmin;
}
.top-content {
  padding: 1.25rem;
  padding-bottom: 2rem;
}
.top-content1 {
  padding: 12px 12px 0 12px;
}
.bottom-content {
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  padding-top: 0.94rem;
  padding-bottom: 3.75rem;
}
.hr-line-div {
  position: relative;
  margin: 0 auto;
  height: 2px;
  width: 100%;
  background: linear-gradient(
    left,
    transparent 0%,
    #00b8af 50%,
    transparent 100%
  );
  animation: scan-move 3s linear infinite;
}
@keyframes scan-move {
  0% {
    top: 0%;
  }
  50% {
    top: 50%;
  }
  100% {
    top: 100%;
  }
}
