import QRCode from "qrcodejs2";
export default {
  data() {
    return {
      amount: "",
      Description: "",
      rules: {
        validAmount: () => {
          return v =>
            /^([1-9]\d*|0)(\.\d{0,20})?$/.test(v) || `${v} is not valid`;
        }
      }
    };
  },
  mounted() {
    this.qrcode();
  },
  methods: {
    qrcode() {
      (document.getElementById("qrcode").innerHTML = ""),
        new QRCode("qrcode", {
          width: 126,
          height: 126,
          text: JSON.stringify({
            address: this.$store.state.wallets[this.$store.state.currentIndex]
              .address,
            amount: this.amount,
            description: this.Description
          })
        });
    }
  }
};
