import transaction from "@/libs1/transaction";
import crypto from "@/libs1/crypto";
import { mapState, mapGetters } from "vuex";

export default {
  data() {
    return {
      fee: 0.1,
      amount: "",
      valid: false,
      sendConfirm: false,
      sendInfo: {},
      recipient: "",
      Description: "",
      networkType: this.$config.NET_BYTE,
      activeTab: 0, //hot or cold
      vsysBalance: 0,
      hotAddrKeyPair: {},
      sendPromp: "",
      rules: {
        maxLength: f => {
          return v => {
            if (v && v.length) {
              return (
                v.length < f || `Description shouldn't be longer than ${f}.`
              );
            } else {
              return true;
            }
          };
        },
        required: f => {
          return v => !!v || `${f} is required`;
        },
        validAddr: netByte => {
          return v => {
            let reg = /[1-9a-km-zA-HJ-NP-Z]/;
            if (v) {
              for (let i of v) {
                if (!reg.test(i)) return "Invalid address";
              }
            }
            return (
              (crypto.isValidAddress(v, netByte) &&
                this.wallets[this.currentIndex].address != v) ||
              "Invalid address"
            );
          };
        },
        validPublicKey: (addr, netByte) => {
          return v =>
            crypto.isValidPublicKey(v, addr, netByte) || "Invalid publicKey";
        },
        maxAmount: f => {
          return v => Number(v) <= Number(f) || "Insufficient Balance";
        },
        validAmount: () => {
          return v => {
            if (v && Number(v)) {
              let a = this.$utils
                .Big(v)
                .times(this.unity)
                .toFixed();
              if (a) {
                if (a.indexOf(".") !== -1) return "Too many decimal";
                if (a.length > 20) return "No longer than 20 bits";
              }
            }
            return /^([1-9]\d*|0)(\.\d{0,20})?$/.test(v) || `${v} is not valid`;
          };
        }
      }
    };
  },
  created() {
    this.getVSYS();
    this.fee =
      window.constants.TOKEN_ID === "VSYS Coin"
        ? window.constants.TX_FEE.Default
        : window.constants.TX_FEE.ContractExec;
    this.precision = this.unity;
    this.recipient = this.$store.state.sendInfo.recipient;
    this.amount = this.$store.state.sendInfo.amount;
    this.Description = this.$store.state.sendInfo.description;
  },

  computed: {
    ...mapState(["wallets", "currentIndex", "unity"]),
    ...mapGetters(["balance"])
  },

  methods: {
    goConfirm() {
      if (!this.$refs.form.validate()) return;
      // Info of Send Confirm Dialog
      this.sendInfo = {
        From: this.wallets[this.currentIndex].address,
        To: this.recipient,
        Type: "Payment",
        Amount: this.amount,
        Fee: this.fee,
        Description: this.Description
      };
      this.sendConfirm = true;
    },
    getVSYS() {
      this.$store.dispatch("update_vsys", this.currentIndex);
    },
    handleCancel() {
      if (this.$store.state.loginType === "send") {
        this.$router.replace({ path: "/home/shift" });
      } else {
        this.$store.commit("reset_sendinfo");
        this.$router.replace({ path: "/home" });
      }
    },
    routerBtn() {
      if (!this.$refs.form.validate()) return;
      if (this.$store.state.tokenid === "VSYS Coin") {
        // const dataInfo = {
        //   recipient: this.recipient,
        //   amount: parseInt(this.amount * this.precision),
        //   fee: parseInt(this.fee * constants.VSYS_PRECISION),
        //   feeScale: constants.FEE_SCALE,
        //   timestamp: Date.now(),
        //   attachment: this.Description
        // };
        // this.doPayment = apiSchema => {
        //   axios
        //     .post(`http://18.218.106.1:9922/vsys/broadcast/payment`, apiSchema)
        //     .then(dealRes)
        //     .catch(dealErr);
        // };
        // if (this.activeTab === 0) {//hot wallet
        //   let apiSchema = transaction.prepareForAPI(
        //     dataInfo,
        //     this.hotAddrKeyPair['AUAztxsft2v6rmjRRb72nLea6BNyRHHWpUR'],
        //     constants.TRANSACTION_TYPE.PAYMENT_TX
        //   );
        //   this.doPayment(apiSchema);
        // } else {
        //   this.txType = constants.TRANSACTION_TYPE.PAYMENT_TX;
        //   this.toSign = dataInfo;
        // }
      } else {
        let contractId = transaction.tokenIdToContractId(this.$config.tokenId);
        console.log(contractId);
        const dataInfo = {
          contractId: contractId,
          functionIndex: this.$store.state.canSplit ? 4 : 3,
          functionData: transaction.prepareTokenSendFuncDataStr(
            this.recipient,
            parseInt(this.amount * this.precision)
          ),
          attachment: this.Description || "",
          fee: parseInt(this.fee * window.constants.VSYS_PRECISION),
          feeScale: window.constants.FEE_SCALE,
          timestamp: Date.now() * 1e6
        };
        //hot wallet
        if (this.activeTab === 0) {
          let apiSchema = transaction.prepareForAPI(
            dataInfo,
            this.wallets[this.currentIndex].keyPair,
            window.constants.TRANSACTION_TYPE.CONTRACT_EXEC_TX
          );
          //update send info in store
          this.$store.commit("update_sendInfo", {
            recipient: this.recipient,
            amount: this.amount,
            description: this.Description || ""
          });
          //transaction broadcast
          this.$axios
            .post(
              `${this.$config.vsysEndPoint}/contract/broadcast/execute`,
              apiSchema
            )
            .then(res => {
              if (res.status === 200) {
                this.$store.commit("update_loginType", "");
                this.$router.replace({
                  path: "/home/send/finish"
                });
              }
            })
            .catch(err => {
              if (err.response && err.response.status === 400) {
                console.log(err.response);
                if (err.response.data.error === 112) {
                  this.sendPromp = "Not enough VSYS.";
                }
              } else {
                this.sendPromp = "Sorry,Transaction form submission failed";
              }
            });
        } else {
          //cold wallet
          this.txType = window.constants.TRANSACTION_TYPE.CONTRACT_EXEC_TX;
          this.toSign = dataInfo;
        }
      }
    }
  }
};
