export default {
  data() {
    return {
      interval: "",
      amount: 0,
      recipient: "",
      timeNumber: 5
    };
  },
  created() {
    console.log("countdown start");
    this.recipient = this.$store.state.sendInfo.recipient;
    this.amount = this.$store.state.sendInfo.amount;
    this.setEndTime();
  },
  methods: {
    setEndTime() {
      let that = this;
      this.interval = setInterval(function countTime() {
        // console.log(that.timeNumber);
        that.timeNumber--;
        if (that.timeNumber <= 0) {
          clearInterval(this.interval);
          that.$store.commit("reset_sendinfo");
          that.$router.replace({ path: "/home" });
        }
      }, 1000);
    }
  },
  beforeDestroy() {
    clearInterval(this.interval);
  }
};
